package java_prorgam_pract;

public class Swapping_variable_3rd_varaible {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String str1="zafar";
		String str2="shareef";
		
		System.out.println("Before Swapping :" + str1 + " "  + str2);
		
		
		String str3= str1;
		str1=str2;
		str2=str3;
		
		System.out.println("After swapping Swapping :" + str1 + " "  + str2);
		
		

	}

}
